import { LessonComponent } from "../Components/Lessons.jsx";
import { scienceLessons, scienceQuizzes } from "../data.js";
import braixen from "../images/dd6f9oj-99e6d1f7-7929-4202-bb07-5555667e9e8c.gif";
import { Button } from "reactstrap";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import delphox from "../images/delphox.webp";

export const LessonsPage = () => {
  const { subject } = useParams();

  const getLessons = () => {
    if (subject === "science") {
      console.log(scienceLessons);
      return scienceLessons;
    } else if (subject === "math") {
      return "No lessons available for math.";
    }
  };
  const lessons = getLessons();
  console.log(lessons);
  return (
    <section className="lessons-section">
      <ul>
        {typeof lessons === Array ? (
          lessons.map((lesson, index) => <li key={index}></li>)
        ) : (
          <li>{lessons}</li>
        )}{" "}
      </ul>
      {typeof lessons !== Array && (
        <Link to={"/"}>
          <Button>Home</Button>
        </Link>
      )}
      <img width="auto" height="300px" src={delphox} alt="delphox" />
    </section>
  );
};
