import { Link, useParams } from "react-router-dom";
import { scienceLessons, scienceQuizzes } from "../data.js";
import { Container, Button, Form, FormGroup, Input, Label } from "reactstrap";
import { useState } from "react";

export const Quiz = () => {
  const { lessonId } = useParams();
  console.log(lessonId);
  const [answers, setAnswers] = useState({});
  const handleChange = (questionId, selectedOption) => {
    setAnswers({
      ...answers,
      [questionId]: selectedOption,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Process and submit answers here
    console.log(answers);
  };
  const quiz = scienceQuizzes.find(
    (quiz) => quiz.lessonId === parseInt(lessonId)
  );
  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        {quiz.questions.map((question, index) => (
          <FormGroup key={index}>
            <Label for={`question${index}`}>{question.question}</Label>
            <Input
              type="select"
              name={`question${index}`}
              id={`question${index}`}
              onChange={(e) =>
                handleChange(question.questionId, e.target.value)
              }
            >
              <option value="">Select an option</option>
              {question.options.map((option, optionIndex) => (
                <option key={optionIndex} value={option}>
                  {option}
                </option>
              ))}
            </Input>
          </FormGroup>
        ))}
        <Button color="primary" type="submit">
          Submit
        </Button>
      </Form>
    </Container>
  );
};
