import { Link, useParams } from "react-router-dom";
import { scienceLessons, scienceQuizzes } from "../data.js";
import { Container, Button, Form, FormGroup, Input, Label } from "reactstrap";
import { useEffect, useState } from "react";

export const Quiz = () => {
  const { lessonId } = useParams();
  console.log(lessonId);
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const handleChange = (questionId, selectedOption) => {
    const answer = { questionId, selectedOption };
    setAnswers({ ...answers, answer });

    // Move to the next question if it's not the last question
    if (currentQuestionIndex < quiz.questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  useEffect(() => {
    console.log(answers);
  }, [answers]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };
  const quiz = scienceQuizzes.find(
    (quiz) => quiz.lessonId === parseInt(lessonId)
  );

  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        {quiz.questions.map((question, index) => (
          <FormGroup key={index}>
            <Label for={`question${index}`}>{question.question}</Label>
            <Input
              type="select"
              name={`question${index}`}
              id={`question${index}`}
              onChange={(e) => handleChange(question.id, e.target.value)}
              disabled={index > currentQuestionIndex} // Disable questions after the current question
              value={answers[index] || ""}
            >
              <option value="">Select an option</option>
              {question.options.map((option, optionIndex) => (
                <option key={optionIndex} value={option}>
                  {option}
                </option>
              ))}
            </Input>
          </FormGroup>
        ))}
        <Button
          color="primary"
          type="submit"
          disabled={currentQuestionIndex < quiz.questions.length - 1}
        >
          Submit
        </Button>
      </Form>

      {submitted && (
        <div>
          <h2>Results:</h2>
          <ul>
            {quiz.questions.map((question, index) => (
              <li key={index}>
                <strong>Question {index + 1}:</strong> Your Answer:{" "}
                {answers[question]}, Correct Answer: {question.correctAnswer}
              </li>
            ))}
          </ul>
        </div>
      )}
    </Container>
  );
};
