import { Link, useParams } from "react-router-dom";
import { scienceQuizzes } from "../data.js";
import { Container, Button, Form, FormGroup, Input, Label } from "reactstrap";
import { useEffect, useState } from "react";

export const Quiz = () => {
  const { lessonId } = useParams();
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (questionId, selectedOption) => {
    const answer = { questionId, selectedOption };
    setAnswers({ ...answers, [questionId]: answer });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };

  const quiz = scienceQuizzes.find(
    (quiz) => quiz.lessonId === parseInt(lessonId)
  );
  const calcGrade = () => {
    let correctAnswers = 0;
    let totalQuestions = quiz.questions.length;
    for (let i = 0; i < totalQuestions; i++) {
      let questionId = quiz.questions[i].id;
      let answer = answers[questionId].selectedOption;
      console.log(answer);
      if (answer === quiz.questions[i].correctAnswer) {
        correctAnswers += 1;
      }
    }
    return (correctAnswers / totalQuestions) * 100;
  };
  useEffect(() => {
    console.log(answers);
  }, [answers]);

  return (
    <Container>
      {submitted ? (
        <div>
          <h2>Results: {calcGrade()} </h2>
          <ul>
            {quiz.questions.map((question) => (
              <li key={question.id}>
                <strong>Question {question.id}:</strong>{" "}
                {answers[question.id].selectedOption === question.correctAnswer
                  ? "Correct ✅"
                  : "Incorrect ❌"}
                <p>Your Answer: </p>
                {answers[question.id].selectedOption}, <p>Correct Answer: </p>
                {question.correctAnswer}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <Form onSubmit={handleSubmit}>
          {quiz.questions.map((question, index) => (
            <FormGroup key={index}>
              <Label for={`question${index}`}>{question.question}</Label>
              <div>
                {question.options.map((option, optionIndex) => (
                  <div key={optionIndex}>
                    <Input
                      type="radio"
                      name={`question${index}`}
                      id={`question${index}_option${optionIndex}`}
                      value={option}
                      onChange={() => handleChange(question.id, option)}
                      checked={
                        answers[question.id]
                          ? answers[question.id].selectedOption === option
                          : false
                      }
                    />
                    <Label for={`question${index}_option${optionIndex}`}>
                      {option}
                    </Label>
                  </div>
                ))}
              </div>
            </FormGroup>
          ))}
          <Button
            color="primary"
            type="submit"
            disabled={Object.keys(answers).length < quiz.questions.length}
          >
            Submit
          </Button>
        </Form>
      )}
    </Container>
  );
};
