import { Link, useParams, useLocation } from "react-router-dom";
import { scienceLessons } from "../data.js";
import { Button } from "reactstrap";
import fennekin from "../images/Fennekin_sprite.webp";

export const Lesson = ({ subject }) => {
  const location = useLocation();
  const { lessonId } = useParams();

  const lesson = scienceLessons.find(
    (lesson) => lesson.id === parseInt(lessonId)
  );
  console.log(lesson.mainPoints);
  return (
    <section>
      <div className="lesson-text">
        <h2>{lesson.title}</h2>
        <p>{lesson.introduction}</p>
        <p>{lesson.mainPoints}</p>
        <Link to={`${location.pathname}/quiz`}>
          <Button>Take Quiz</Button>
        </Link>
      </div>
      <img src={fennekin} alt="Fennekin" className="lesson-image" />
    </section>
  );
};
