import { useParams } from "react-router-dom";
import { scienceLessons, scienceQuizzes } from "../ScienceData.js";

export const LessonComponent = () => {
  const { lessonId } = useParams();
  const getLesson = (lessonId) => {
    return ScienceData.find((lesson) => lesson.id === lessonId);
  };
  return (
    <div>
      <h2>{lesson.title}</h2>
      <p>{lesson.introduction}</p>
      <ul>{lesson.mainPoints}</ul>
    </div>
  );
};
