import { Link, useParams } from "react-router-dom";
import { scienceQuizzes } from "../data.js";
import { Container, Button, Form, FormGroup, Input, Label } from "reactstrap";
import { useEffect, useState } from "react";

export const Quiz = () => {
  const { lessonId } = useParams();
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const handleChange = (questionId, selectedOption) => {
    const answer = { questionId, selectedOption };
    setAnswers({ ...answers, [questionId]: answer });

    // Move to the next question if it's not the last question
    if (currentQuestionIndex < quiz.questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };

  const quiz = scienceQuizzes.find(
    (quiz) => quiz.lessonId === parseInt(lessonId)
  );

  useEffect(() => {
    console.log(answers);
  }, [answers]);

  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        {quiz.questions.map((question, index) => (
          <FormGroup key={index}>
            <Label for={`question${index}`}>{question.question}</Label>
            <div>
              {question.options.map((option, optionIndex) => (
                <div key={optionIndex}>
                  <Input
                    type="radio"
                    name={`question${index}`}
                    id={`question${index}_option${optionIndex}`}
                    value={option}
                    onChange={() => handleChange(question.id, option)}
                    disabled={index > currentQuestionIndex}
                    checked={
                      answers[question.id]
                        ? answers[question.id].selectedOption === option
                        : false
                    }
                  />
                  <Label for={`question${index}_option${optionIndex}`}>
                    {option}
                  </Label>
                </div>
              ))}
            </div>
          </FormGroup>
        ))}
        <Button
          color="primary"
          type="submit"
          disabled={currentQuestionIndex < quiz.questions.length - 1}
        >
          Submit
        </Button>
      </Form>

      {submitted && (
        <div>
          <h2>Results:</h2>
          <ul>
            {quiz.questions.map((question) => (
              <li key={question.id}>
                <strong>Question {question.id}:</strong> Your Answer:{" "}
                {answers[question.id].selectedOption}, Correct Answer:{" "}
                {question.correctAnswer}
              </li>
            ))}
          </ul>
        </div>
      )}
    </Container>
  );
};
