import { Link, useParams } from "react-router-dom";
import { scienceLessons, scienceQuizzes } from "../data.js";
import { Container, Button, Form, FormGroup, Input, Label } from "reactstrap";
import { useState } from "react";

export const Quiz = () => {
  const { lessonId } = useParams();
  console.log(lessonId);
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (questionId, selectedOption) => {
    console.log(answers);
    console.log(selectedOption);
    setAnswers({
      ...answers,
      [questionId]: selectedOption,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Process and submit answers here
    setSubmitted(true);
  };
  const quiz = scienceQuizzes.find(
    (quiz) => quiz.lessonId === parseInt(lessonId)
  );

  let count = 0;
  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        {quiz.questions.map((question, index) => (
          <FormGroup key={index}>
            <Label for={`question${index}`}>{question.question}</Label>
            <Input
              type="select"
              name={`question${index}`}
              id={`question${index}`}
              onChange={(e) => handleChange(count, e.target.value)}
            >
              <option value="">Select an option</option>
              {question.options.map((option, optionIndex) => (
                <option key={optionIndex} value={option}>
                  {option}
                </option>
              ))}
            </Input>
          </FormGroup>
        ))}
        <Button color="primary" type="submit">
          Submit
        </Button>
      </Form>
      {submitted && (
        <div>
          <h2>Results:</h2>
          <ul>
            {quiz.questions.map((question, index) => (
              <li key={index}>
                <strong>Question {index + 1}:</strong> Your Answer:{" "}
                {answers[question.questionId]}, Correct Answer:{" "}
                {question.correctAnswer}
              </li>
            ))}
          </ul>
        </div>
      )}
    </Container>
  );
};
